<script lang="ts" ✂prettier:content✂=",">{}</script>

<div class="event {panelOpen ? 'open' : ''}">
    {#if event.deleted}
        <div class="restore-menu">
            <i>'{event.name}' has been deleted</i>
            <button class="primary unbordered" on:click="{restoreEvent}">
                <Restore />
                Undo Deletion
            </button>
        </div>
    {:else if panelOpen}
        <div class="header">
            <button
                aria-label="{obfuscated ? 'Show entry' : 'Hide entry'}"
                on:click="{() => (obfuscated = !obfuscated)}"
            >
                {#if obfuscated}
                    <Eye size="25" />
                {:else}
                    <EyeOff size="25" />
                {/if}
            </button>
            {#if !obfuscated}
                <button class="danger" on:click="{deleteEvent}">
                    <Bin size="25" />
                </button>
                {#if editingLabel}
                    <div class="flex-center">
                        <LabelSelect
                            on:change="{updateLabel}"
                            value="{event.label?.id || ''}"
                            labels="{labels}"
                            auth="{auth}"
                        />
                        <button
                            on:click="{() => (editingLabel = false)}"
                            class="icon-button"
                        >
                            <PencilOff size="20" />
                        </button>
                    </div>
                {:else if event.label}
                    <span>
                        <Label
                            obfuscated="{obfuscated}"
                            label="{event.label}"
                        />
                        <button on:click="{() => (editingLabel = true)}">
                            <Pencil size="15" />
                        </button>
                    </span>
                {:else}
                    <button
                        class="link"
                        on:click="{() => (editingLabel = true)}"
                    >
                        Add Label
                    </button>
                {/if}
            {/if}
            <button on:click="{() => (panelOpen = false)}" class="icon-button">
                <ChevronUp size="25" />
            </button>
        </div>
        <i>
            Created
            <!-- TODO use tz from db -->
            <UtcTime timestamp="{event.created}" fmt="hh:mm DD/MM/YYYY" />
        </i>
        <div class="middle-row">
            {#if obfuscated}
                <p class="event-name-inp obfuscated">
                    {obfuscate(event.name)}
                </p>
            {:else}
                <input
                    bind:this="{nameInput}"
                    class="editable-text event-name-inp"
                    on:change="{updateName}"
                    placeholder="Event Name"
                    value="{event.name}"
                />
            {/if}
        </div>
        <div class="from-to-menu">
            {#if Event.isInstantEvent(event)}
                {#if obfuscated}
                    <div>
                        <span>
                            <UtcTime
                                timestamp="{event.start}"
                                fmt="DD/MM/YYYY HH:mm"
                            />
                        </span>
                    </div>
                {:else}
                    <div>
                        <input
                            class="editable-text"
                            on:change="{updateStartAndEnd}"
                            placeholder="Start"
                            type="datetime-local"
                            value="{fmtTimestampForInput(event.start)}"
                        />
                    </div>
                    <button
                        class="instant-duration-toggle"
                        on:click="{makeDurationEvent}"
                        use:tooltip="{{
                            content: 'Give this event a duration'
                        }}"
                    >
                        <TimelineOutline size="30" />
                        <span class="instant-duration-toggle-text">
                            Make Duration Event
                        </span>
                    </button>
                {/if}
            {:else if obfuscated}
                <div>
                    from
                    <span>
                        <UtcTime
                            timestamp="{event.start}"
                            fmt="DD/MM/YYYY HH:mm"
                        />
                    </span>
                </div>
                <div>
                    to
                    <span>
                        <UtcTime
                            timestamp="{event.end}"
                            fmt="DD/MM/YYYY HH:mm"
                        />
                    </span>
                </div>
            {:else}
                <div>
                    from
                    <input
                        class="editable-text"
                        on:change="{updateStart}"
                        placeholder="Start"
                        type="datetime-local"
                        value="{fmtTimestampForInput(event.start)}"
                    />
                </div>
                <div>
                    to
                    <input
                        class="editable-text"
                        on:change="{updateEnd}"
                        placeholder="End"
                        type="datetime-local"
                        value="{fmtTimestampForInput(event.end)}"
                    />
                </div>
                <p>
                    <i>
                        ({fmtDuration(event.end - event.start)})
                    </i>
                </p>
                <div>
                    <button
                        class="instant-duration-toggle"
                        on:click="{makeInstantEvent}"
                        use:tooltip="{{
                            content: 'Make this event instantaneous'
                        }}"
                    >
                        <TimelineClockOutline size="30" />
                        <span class="instant-duration-toggle-text">
                            Make Instant Event
                        </span>
                    </button>
                </div>
            {/if}
        </div>
    {:else}
        <button
            aria-label="{obfuscated ? 'Show entry' : 'Hide entry'}"
            on:click="{() => (obfuscated = !obfuscated)}"
        >
            {#if obfuscated}
                <Eye size="25" />
            {:else}
                <EyeOff size="25" />
            {/if}
        </button>

        {#if obfuscated}
            <p class="event-name-inp obfuscated">
                {obfuscate(event.name)}
            </p>
        {:else}
            <input
                bind:this="{nameInput}"
                class="editable-text event-name-inp"
                on:change="{updateName}"
                placeholder="Event Name"
                value="{event.name}"
            />
        {/if}
        <div class="label-and-open-container">
            {#if obfuscated}
                <span
                    class="entry-label-colour big"
                    style="background: {event.label?.colour || 'transparent'}"
                ></span>
            {:else if event.label}
                <span
                    class="entry-label-colour big"
                    style="background: {event.label.colour || 'transparent'}"
                    use:tooltip="{{ content: event.label.name }}"></span>
            {:else}
                <span class="entry-label-colour big"></span>
            {/if}

            <div>
                <button
                    on:click="{() => (panelOpen = true)}"
                    class="icon-button"
                >
                    <ChevronDown size="25" />
                </button>
            </div>
        </div>
    {/if}
</div>

<style lang="less" ✂prettier:content✂=""></style>